/**
 * @param {string} word
 * @return {number}
 */
// 字符串中不同整数的数目
// 这个方法需要循环一次字符串，性能不好
// 92 ms, 在所有 JavaScript 提交中击败了33.46%
const numDifferentIntegers = function(word) {
  const len = word.length;
  let numStr = '';
  const res = new Set();
  const handleStr = function(str) {
    if (str === '') return;
    str = str.replace(/^0+/ig, '');
    res.add(str);
  };
  for (let i = 0; i < len; i++) {
    const item = word[i];
    // check isNumber
    if (item > -1) {
      numStr = numStr + item;
    } else {
      handleStr(numStr);
      numStr = '';
    }
  }
  handleStr(numStr);
  return res.size;
};

