import { fractionToDecimal } from '../src/0166-fractionToDecimal';

test('166', () => {
  expect(fractionToDecimal(1, 2)).toEqual("0.5");
  expect(fractionToDecimal(2, 1)).toEqual("2");
  expect(fractionToDecimal(1, 5)).toEqual("0.2");
  expect(fractionToDecimal(35, 20)).toEqual("1.75");

  expect(fractionToDecimal(2, 3)).toEqual("0.(6)");
  expect(fractionToDecimal(4, 333)).toEqual("0.(012)");
  expect(fractionToDecimal(821, 370)).toEqual("2.2(189)");

  expect(fractionToDecimal(-1, -2147483648)).toEqual("0.0000000004656612873077392578125");
  expect(fractionToDecimal(1, 214748364)).toEqual("0.00()");
});
